def filter_sql(self, sql='', limit_num=0):
    sql_lower = sql.lower()
    # 对查询sql增加limit限制
    if re.match(r"^select", sql_lower):
       sql=f"select a.* from ({sql.rstrip(';')}) a WHERE ROWNUM <= {limit_num}"
    return sql.strip()

