def decode(text, encodings=["utf-8", "ascii"], force=False):
    for encoding in encodings:
        try:
            return text.decode(encoding)
        except ValueError as e:
            pass

    if force:
        for encoding in encodings:
            try:
                return text.decode(encoding, 'ignore')
